// PostFlow FINAL WORKING - Based on user's successful config
const items = $input.all();
const readyPosts = [];

console.log(`üöÄ FINAL WORKING WORKFLOW - Processing ${items.length} items (property_* format confirmed)`);

// Get current date/time for comparison
const now = new Date();
console.log(`üïí Current time: ${now.toISOString()}`);
console.log(`üåç Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type from binary data
function getMediaTypeFromBinary(binaryData, fileName) {
  // Check mime type first (most reliable)
  if (binaryData && binaryData.mimeType) {
    const mimeType = binaryData.mimeType.toLowerCase();
    if (mimeType.startsWith('video/')) {
      console.log(`üé¨ Detected VIDEO from mime type: ${mimeType}`);
      return 'VIDEO';
    }
    if (mimeType.startsWith('image/')) {
      console.log(`üì∏ Detected IMAGE from mime type: ${mimeType}`);
      return 'IMAGE';
    }
  }

  // Fallback to filename extension
  if (fileName) {
    const name = fileName.toLowerCase();
    const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];

    for (const ext of videoExtensions) {
      if (name.includes(ext)) {
        console.log(`üé¨ Detected VIDEO from filename: ${fileName}`);
        return 'VIDEO';
      }
    }
  }

  console.log(`üì∏ Detected IMAGE (default): ${fileName}`);
  return 'IMAGE';
}

for (const item of items) {
  const data = item.json;
  const binaryData = item.binary || {};

  console.log('\n=== üöÄ Processing Notion page (WORKING VERSION) ===');
  console.log('Raw data structure:', JSON.stringify(Object.keys(data), null, 2));

  // Use correct property_* format confirmed by user
  const postTitle = data.property_nome || data.name || 'Untitled Post';
  console.log(`üìù Post: "${postTitle}"`);
  console.log(`üÜî Page ID: ${data.id}`);

  // Check all conditions using property_* format
  const status = data.property_status;
  console.log(`üìä Status: ${status}`);
  if (status !== 'Agendado') {
    console.log(`‚ùå SKIP: Status is "${status}", not "Agendado"`);
    continue;
  }

  const shouldPublish = data.property_publicar;
  console.log(`‚úÖ Publicar?: ${shouldPublish}`);
  if (!shouldPublish) {
    console.log('‚ùå SKIP: Publicar? is false');
    continue;
  }

  // Handle platform array format (confirmed from user data)
  const platforms = data.property_plataforma || [];
  let platformNames = [];
  let hasInstagram = false;

  if (Array.isArray(platforms)) {
    platformNames = platforms;
    hasInstagram = platforms.includes('Instagram');
  } else if (typeof platforms === 'string') {
    platformNames = [platforms];
    hasInstagram = platforms === 'Instagram';
  }

  console.log(`üéØ Platforms: [${platformNames.join(', ')}]`);

  if (!hasInstagram) {
    console.log('‚ùå SKIP: No Instagram platform');
    continue;
  }

  // Check scheduled date using property_data_do_agendamento
  const scheduledDateData = data.property_data_do_agendamento;
  console.log(`üìÖ Scheduled date data:`, scheduledDateData);

  if (!scheduledDateData) {
    console.log('‚ùå SKIP: No scheduled date set');
    continue;
  }

  // Parse scheduled date (handle user's confirmed format)
  let scheduledDate;

  if (typeof scheduledDateData === 'object' && scheduledDateData.start) {
    const scheduledStart = scheduledDateData.start;
    if (scheduledStart.includes('T')) {
      // Has time component (like: 2025-09-01T21:53:00.000-03:00)
      scheduledDate = new Date(scheduledStart);
    } else {
      // Date only - assume 9:00 AM local time
      scheduledDate = new Date(scheduledStart + 'T09:00:00');
    }
  } else if (typeof scheduledDateData === 'string') {
    scheduledDate = new Date(scheduledDateData);
  } else {
    console.log('‚ùå SKIP: Invalid scheduled date format');
    continue;
  }

  console.log(`üìÖ Scheduled for: ${scheduledDate.toISOString()}`);
  console.log(`üìÖ Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

  // Check if it's time to publish
  const isTimeToPublish = now >= scheduledDate;
  console.log(`‚è∞ Time check: ${isTimeToPublish ? '‚úÖ READY' : '‚ùå NOT YET'}`);

  if (!isTimeToPublish) {
    const timeUntil = scheduledDate - now;
    const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
    console.log(`‚è≥ SKIP: Still ${hoursUntil} hours until scheduled time`);
    continue;
  }

  // Extract post data using property_* format
  const postText = data.property_texto_do_post || '';

  // Handle hashtags array format (confirmed from user data)
  let hashtags = '';
  if (data.property_hashtags) {
    if (Array.isArray(data.property_hashtags)) {
      hashtags = data.property_hashtags.map(tag => tag.startsWith('#') ? tag : `#${tag}`).join(' ');
    } else if (typeof data.property_hashtags === 'string') {
      hashtags = data.property_hashtags.startsWith('#') ? data.property_hashtags : `#${data.property_hashtags}`;
    }
  }

  // Check for binary data (files downloaded by Notion)
  console.log(`üì¶ Binary data keys:`, Object.keys(binaryData));

  if (Object.keys(binaryData).length === 0) {
    console.log(`‚ùå SKIP: Post "${postTitle}" has no binary data`);
    continue;
  }

  // Get the first binary file (user confirmed binary_key: "M√≠dia_0")
  const binaryKeys = Object.keys(binaryData);
  const firstBinaryKey = binaryKeys[0]; // This should be "M√≠dia_0" as confirmed
  const mediaData = binaryData[firstBinaryKey];

  if (!mediaData) {
    console.log(`‚ùå SKIP: No media data found in binary key: ${firstBinaryKey}`);
    continue;
  }

  const fileName = mediaData.fileName || mediaData.name || 'media_file';
  const fileSize = mediaData.fileSize || 0;
  const mimeType = mediaData.mimeType || 'unknown';

  console.log(`üìÅ Binary file: ${fileName}`);
  console.log(`üìä File info: ${mimeType}, ${Math.round(fileSize/1024/1024*100)/100} MB`);

  // Detect media type (user confirmed: media_type: "VIDEO")
  const mediaType = getMediaTypeFromBinary(mediaData, fileName);
  const caption = postText + (hashtags ? '\n\n' + hashtags : '');

  console.log(`üöÄ POST APPROVED: "${postTitle}" ready for Instagram ${mediaType} posting!`);
  console.log(`üìÅ File: ${fileName} (${Math.round(fileSize/1024/1024*100)/100}MB)`);

  readyPosts.push({
    notion_page_id: data.id,
    post_title: postTitle,
    caption: caption,
    media_type: mediaType,
    file_name: fileName,
    file_size: fileSize,
    mime_type: mimeType,
    scheduled_date: scheduledDate.toISOString(),
    binary_key: firstBinaryKey,
    // Preserve original data
    _preserve_notion_id: data.id,
    _preserve_title: postTitle
  });
}

console.log(`\nüöÄ === FINAL WORKING RESULT ===`);
console.log(`üìä Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('üöÄ Posts that will be published NOW (WORKING):');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (${Math.round(post.file_size/1024/1024*100)/100}MB) (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('‚è≥ No posts ready to publish at this time. Next check in 30 seconds.');
}

// Return data with binary preserved correctly (matching user's working format)
return readyPosts.map((post, index) => {
  // Find the correct item that matches this post
  const matchingItem = items.find(item => item.json.id === post.notion_page_id);

  return {
    json: post,
    binary: {
      data: matchingItem ? matchingItem.binary[post.binary_key] : null
    }
  };
});
