// PostFlow FINAL - Baseado no workflow que funcionava + Notion nativo
const items = $input.all();
const readyPosts = [];

console.log(`ðŸš€ FINAL WORKFLOW - Processing ${items.length} items (Notion native + filters)`);

// Get current date/time for comparison
const now = new Date();
console.log(`ðŸ•’ Current time: ${now.toISOString()}`);
console.log(`ðŸŒ Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type from binary data
function getMediaTypeFromBinary(binaryData, fileName) {
  // Check mime type first (most reliable)
  if (binaryData && binaryData.mimeType) {
    const mimeType = binaryData.mimeType.toLowerCase();
    if (mimeType.startsWith('video/')) {
      console.log(`ðŸŽ¬ Detected VIDEO from mime type: ${mimeType}`);
      return 'VIDEO';
    }
    if (mimeType.startsWith('image/')) {
      console.log(`ðŸ“¸ Detected IMAGE from mime type: ${mimeType}`);
      return 'IMAGE';
    }
  }

  // Fallback to filename extension
  if (fileName) {
    const name = fileName.toLowerCase();
    const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];

    for (const ext of videoExtensions) {
      if (name.includes(ext)) {
        console.log(`ðŸŽ¬ Detected VIDEO from filename: ${fileName}`);
        return 'VIDEO';
      }
    }
  }

  console.log(`ðŸ“¸ Detected IMAGE (default): ${fileName}`);
  return 'IMAGE';
}

for (const item of items) {
  const data = item.json;
  const binaryData = item.binary || {};

  console.log('\n=== ðŸš€ Processing Notion page (FINAL) ===');

  const postTitle = data.properties?.Nome?.title?.[0]?.text?.content || 'Untitled Post';
  console.log(`ðŸ“ Post: "${postTitle}"`);
  console.log(`ðŸ†” Page ID: ${data.id}`);

  // Triple check all conditions (mesmo com filtros do Notion)
  const status = data.properties?.Status?.select?.name;
  console.log(`ðŸ“Š Status: ${status}`);
  if (status !== 'Agendado') {
    console.log(`âŒ SKIP: Status is "${status}", not "Agendado"`);
    continue;
  }

  const shouldPublish = data.properties?.['Publicar?']?.checkbox;
  console.log(`âœ… Publicar?: ${shouldPublish}`);
  if (!shouldPublish) {
    console.log('âŒ SKIP: Publicar? is false');
    continue;
  }

  const platforms = data.properties?.Plataforma?.multi_select || [];
  const platformNames = platforms.map(p => p.name);
  console.log(`ðŸŽ¯ Platforms: [${platformNames.join(', ')}]`);
  const hasInstagram = platforms.some(p => p.name === 'Instagram');

  if (!hasInstagram) {
    console.log('âŒ SKIP: No Instagram platform');
    continue;
  }

  // Check scheduled date/time
  const scheduledDateData = data.properties?.['Data do Agendamento']?.date;
  console.log(`ðŸ“… Scheduled date data:`, scheduledDateData);

  if (!scheduledDateData) {
    console.log('âŒ SKIP: No scheduled date set');
    continue;
  }

  // Parse scheduled date (can be date-only or datetime)
  const scheduledStart = scheduledDateData.start;
  let scheduledDate;

  if (scheduledStart.includes('T')) {
    // Has time component
    scheduledDate = new Date(scheduledStart);
  } else {
    // Date only - assume 9:00 AM local time
    scheduledDate = new Date(scheduledStart + 'T09:00:00');
  }

  console.log(`ðŸ“… Scheduled for: ${scheduledDate.toISOString()}`);
  console.log(`ðŸ“… Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

  // Check if it's time to publish
  const isTimeToPublish = now >= scheduledDate;
  console.log(`â° Time check: ${isTimeToPublish ? 'âœ… READY' : 'âŒ NOT YET'}`);

  if (!isTimeToPublish) {
    const timeUntil = scheduledDate - now;
    const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
    console.log(`â³ SKIP: Still ${hoursUntil} hours until scheduled time`);
    continue;
  }

  // Extract post data
  const postText = data.properties?.['Texto do Post']?.rich_text?.[0]?.text?.content || '';
  const hashtags = data.properties?.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';

  // Check for binary data (files downloaded by Notion)
  console.log(`ðŸ“¦ Binary data keys:`, Object.keys(binaryData));

  if (Object.keys(binaryData).length === 0) {
    console.log(`âŒ SKIP: Post "${postTitle}" has no binary data`);
    continue;
  }

  // Get the first binary file (usually 'data' key or similar)
  const binaryKeys = Object.keys(binaryData);
  const firstBinaryKey = binaryKeys[0];
  const mediaData = binaryData[firstBinaryKey];

  if (!mediaData) {
    console.log(`âŒ SKIP: No media data found in binary key: ${firstBinaryKey}`);
    continue;
  }

  const fileName = mediaData.fileName || mediaData.name || 'media_file';
  const fileSize = mediaData.fileSize || 0;
  const mimeType = mediaData.mimeType || 'unknown';

  console.log(`ðŸ“ Binary file: ${fileName}`);
  console.log(`ðŸ“Š File info: ${mimeType}, ${Math.round(fileSize/1024/1024*100)/100} MB`);

  // Detect media type
  const mediaType = getMediaTypeFromBinary(mediaData, fileName);
  const caption = postText + (hashtags ? '\n\n' + hashtags : '');

  console.log(`ðŸš€ POST APPROVED: "${postTitle}" ready for Instagram ${mediaType} posting!`);
  console.log(`ðŸ“ File: ${fileName} (${Math.round(fileSize/1024/1024*100)/100}MB)`);

  readyPosts.push({
    notion_page_id: data.id,
    post_title: postTitle,
    caption: caption,
    media_type: mediaType,
    file_name: fileName,
    file_size: fileSize,
    mime_type: mimeType,
    scheduled_date: scheduledDate.toISOString(),
    binary_key: firstBinaryKey,
    // Preserve original data
    _preserve_notion_id: data.id,
    _preserve_title: postTitle
  });
}

console.log(`\nðŸš€ === FINAL WORKFLOW RESULT ===`);
console.log(`ðŸ“Š Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('ðŸš€ Posts that will be published NOW (FINAL):');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (${Math.round(post.file_size/1024/1024*100)/100}MB) (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('â³ No posts ready to publish at this time. Next check in 30 seconds.');
}

// Return data with binary preserved correctly
return readyPosts.map((post, index) => ({
  json: post,
  binary: {
    data: items[index].binary[post.binary_key]
  }
}));

// Preserve notion_page_id from original data
const currentData = $json;
const originalData = $('Process Posts (Final)').item.json;

console.log('ðŸ”„ Preserving notion_page_id...');
console.log('Current data (Instagram):', JSON.stringify(currentData, null, 2));
console.log('Original data (Notion):', JSON.stringify(originalData, null, 2));

// Preserve notion_page_id from original data
const finalData = {
  ...currentData,
  notion_page_id: originalData.notion_page_id,
  post_title: originalData.post_title,
  media_type: originalData.media_type,
  instagram_published: true,
  automated_publish: true,
  published_at: new Date().toISOString()
};

console.log('âœ… Final data for Notion update:', JSON.stringify(finalData, null, 2));

return { json: finalData };
