// PostFlow BETA - Process Instagram posts with PHOTOS + VIDEOS support (File Upload)
const items = $input.all();
const readyPosts = [];

console.log(`ðŸŽ¬ BETA FILE UPLOAD - Processing ${items.length} items (Photos + Videos support)`);

// Get current date/time for comparison
const now = new Date();
console.log(`ðŸ•’ Current time: ${now.toISOString()}`);
console.log(`ðŸŒ Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type from file extension
function getMediaType(mediaUrl) {
  const url = mediaUrl.toLowerCase();
  const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];
  const imageExtensions = ['.jpg', '.jpeg', '.png', '.webp', '.gif'];

  for (const ext of videoExtensions) {
    if (url.includes(ext)) return 'VIDEO';
  }

  for (const ext of imageExtensions) {
    if (url.includes(ext)) return 'IMAGE';
  }

  // If no clear extension, assume image (default Instagram behavior)
  return 'IMAGE';
}

// Helper function to convert Google Drive URLs for download
function convertGoogleDriveUrl(url) {
  const driveMatch = url.match(/\/file\/d\/([a-zA-Z0-9_-]+)/);
  if (driveMatch) {
    console.log(`ðŸ”„ Converting Google Drive URL to direct download`);
    return `https://drive.google.com/uc?export=download&id=${driveMatch[1]}`;
  }
  return url; // Return original if not Google Drive
}

// Helper function to check if URL needs downloading
function needsDownload(url) {
  // Google Drive URLs always need downloading
  if (url.includes('drive.google.com')) return true;

  // Notion temporary URLs need to be downloaded  
  if (url.includes('prod-files-secure.s3.us-west-2.amazonaws.com')) return true;

  // Other cloud storage that might need downloading
  if (url.includes('dropbox.com') || url.includes('onedrive.com')) return true;

  // Direct media URLs can potentially be used directly (but may still fail)
  return false;
}

for (const item of items) {
  const responseData = item.json;

  // Get results from Notion API response
  const notionPages = responseData.results || [];
  console.log(`ðŸ“„ Found ${notionPages.length} pages in Notion response`);

  if (notionPages.length === 0) {
    console.log('ðŸ“­ No scheduled posts found. Workflow will finish gracefully.');
    return [];
  }

  for (const data of notionPages) {
    console.log('\n=== ðŸ“‹ Processing page ===');

    // Extract post title first for better logging
    const postTitle = data.properties.Nome?.title?.[0]?.text?.content || 'Untitled Post';
    console.log(`ðŸ“ Post: "${postTitle}"`);
    console.log(`ðŸ†” Page ID: ${data.id}`);

    // Triple check all conditions
    const status = data.properties.Status?.select?.name;
    console.log(`ðŸ“Š Status: ${status}`);
    if (status !== 'Agendado') {
      console.log(`âŒ SKIP: Status is "${status}", not "Agendado"`);
      continue;
    }

    const shouldPublish = data.properties['Publicar?']?.checkbox;
    console.log(`âœ… Publicar?: ${shouldPublish}`);
    if (!shouldPublish) {
      console.log('âŒ SKIP: Publicar? is false');
      continue;
    }

    const platforms = data.properties.Plataforma?.multi_select || [];
    const platformNames = platforms.map(p => p.name);
    console.log(`ðŸŽ¯ Platforms: [${platformNames.join(', ')}]`);
    const hasInstagram = platforms.some(p => p.name === 'Instagram');

    if (!hasInstagram) {
      console.log('âŒ SKIP: No Instagram platform');
      continue;
    }

    // Check scheduled date/time
    const scheduledDateData = data.properties['Data do Agendamento']?.date;
    console.log(`ðŸ“… Scheduled date data:`, scheduledDateData);

    if (!scheduledDateData) {
      console.log('âŒ SKIP: No scheduled date set');
      continue;
    }

    // Parse scheduled date (can be date-only or datetime)
    const scheduledStart = scheduledDateData.start;
    let scheduledDate;

    if (scheduledStart.includes('T')) {
      // Has time component
      scheduledDate = new Date(scheduledStart);
    } else {
      // Date only - assume 9:00 AM local time
      scheduledDate = new Date(scheduledStart + 'T09:00:00');
    }

    console.log(`ðŸ“… Scheduled for: ${scheduledDate.toISOString()}`);
    console.log(`ðŸ“… Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

    // Check if it's time to publish
    const isTimeToPublish = now >= scheduledDate;
    console.log(`â° Time check: ${isTimeToPublish ? 'âœ… READY' : 'âŒ NOT YET'}`);

    if (!isTimeToPublish) {
      const timeUntil = scheduledDate - now;
      const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
      console.log(`â³ SKIP: Still ${hoursUntil} hours until scheduled time`);
      continue;
    }

    // Extract post data
    const postText = data.properties['Texto do Post']?.rich_text?.[0]?.text?.content || '';
    const hashtags = data.properties.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';
    const media = data.properties.MÃ­dia?.files?.[0];

    console.log(`ðŸ“¸ Media available: ${!!media}`);

    if (!media) {
      console.log(`âŒ SKIP: Post "${postTitle}" has no media`);
      continue;
    }

    let mediaUrl = media.external?.url || media.file?.url;
    console.log(`ðŸ”— Original Media URL: ${mediaUrl}`);

    // Convert Google Drive URLs for download
    const downloadUrl = convertGoogleDriveUrl(mediaUrl);
    console.log(`ðŸ”— Download URL: ${downloadUrl}`);

    // Detect media type (photo vs video)
    const mediaType = getMediaType(downloadUrl);
    console.log(`ðŸŽ¬ Media Type: ${mediaType}`);

    // Check if we need to download the file
    const requiresDownload = needsDownload(downloadUrl);
    console.log(`ðŸ’¾ Requires Download: ${requiresDownload}`);

    // Build caption
    const caption = postText + (hashtags ? '\n\n' + hashtags : '');

    console.log(`ðŸš€ POST APPROVED: "${postTitle}" ready for Instagram ${mediaType} posting!`);
    console.log(`ðŸ•’ Publishing at scheduled time: ${scheduledDate.toLocaleString('pt-BR')}`);

    readyPosts.push({
      notion_page_id: data.id,
      post_title: postTitle,
      caption: caption,
      media_url: mediaUrl,
      download_url: downloadUrl,
      media_type: mediaType,
      requires_download: requiresDownload,
      scheduled_date: scheduledDate.toISOString(),
      // Dados extras para preservar atravÃ©s do fluxo
      _preserve_notion_id: data.id,
      _preserve_title: postTitle
    });
  }
}

console.log(`\nðŸŽ¯ === BETA FILE UPLOAD EXECUTION RESULT ===`);
console.log(`ðŸ“Š Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('ðŸš€ Posts that will be published NOW (BETA):');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (download: ${post.requires_download}) (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('â³ No posts ready to publish at this time. Next check in 30 seconds.');
}

return readyPosts.map(post => ({ json: post }));

// Recuperar notion_page_id dos dados originais
const currentData = $json;
const originalData = $('Process Instagram Posts (BETA)').item.json;

console.log('ðŸ”„ Recuperando notion_page_id...');
console.log('Dados atuais (Instagram):', JSON.stringify(currentData, null, 2));
console.log('Dados originais (Notion):', JSON.stringify(originalData, null, 2));

// Preservar notion_page_id dos dados originais
const finalData = {
  ...currentData,
  notion_page_id: originalData.notion_page_id,
  post_title: originalData.post_title,
  media_type: originalData.media_type,
  instagram_published: true,
  automated_publish: true,
  published_at: new Date().toISOString()
};

console.log('âœ… Dados finais para Notion:', JSON.stringify(finalData, null, 2));

return { json: finalData };
