// PostFlow Manual - Process Instagram posts from Notion HTTP Response
const items = $input.all();
const readyPosts = [];

console.log(`Processing ${items.length} items from HTTP Request`);

for (const item of items) {
  const responseData = item.json;
  console.log('Full HTTP Response:', JSON.stringify(responseData, null, 2));

  // Get results from Notion API response
  const notionPages = responseData.results || [];
  console.log(`Found ${notionPages.length} pages in Notion response`);

  for (const data of notionPages) {
    console.log('Processing page:', JSON.stringify(data, null, 2));

    // Check if post is for Instagram
    const platforms = data.properties.Plataforma?.multi_select || [];
    console.log('Platforms found:', platforms);
    const hasInstagram = platforms.some(p => p.name === 'Instagram');

    if (!hasInstagram) {
      console.log('Skipping - no Instagram platform');
      continue;
    }

    // Extract post data
    const postTitle = data.properties.Nome?.title?.[0]?.text?.content || 'Untitled Post';
    const postText = data.properties['Texto do Post']?.rich_text?.[0]?.text?.content || '';
    const hashtags = data.properties.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';
    const media = data.properties.MÃ­dia?.files?.[0];

    console.log('Post title:', postTitle);
    console.log('Media found:', media);

    if (!media) {
      console.log(`Post "${postTitle}" has no media`);
      continue;
    }

    const mediaUrl = media.external?.url || media.file?.url;
    console.log('Media URL:', mediaUrl);

    // Build caption
    const caption = postText + (hashtags ? '\n\n' + hashtags : '');

    readyPosts.push({
      notion_page_id: data.id,
      post_title: postTitle,
      caption: caption,
      media_url: mediaUrl
    });
  }
}

console.log(`Found ${readyPosts.length} posts ready for Instagram`);
return readyPosts.map(post => ({ json: post }));
