// PostFlow SIMPLES - Fotos + Vídeos
const items = $input.all();
const readyPosts = [];

console.log(`🤖 SIMPLE EXECUTION - Processing ${items.length} items`);

// Get current date/time for comparison
const now = new Date();
console.log(`🕒 Current time: ${now.toISOString()}`);
console.log(`🌍 Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type
function getMediaType(mediaUrl) {
  const url = mediaUrl.toLowerCase();
  const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];

  for (const ext of videoExtensions) {
    if (url.includes(ext)) return 'VIDEO';
  }

  return 'IMAGE'; // default to image
}

// Convert Google Drive URLs to download format
function prepareMediaUrl(originalUrl) {
  const url = originalUrl.toLowerCase();

  // Google Drive URLs - convert to direct download
  if (url.includes('drive.google.com')) {
    const driveMatch = originalUrl.match(/\/file\/d\/([a-zA-Z0-9_-]+)/);
    if (driveMatch) {
      console.log(`🔄 Converting Google Drive URL`);
      return `https://drive.google.com/uc?export=download&id=${driveMatch[1]}`;
    }
  }

  return originalUrl;
}

for (const item of items) {
  const responseData = item.json;
  const notionPages = responseData.results || [];

  console.log(`📄 Found ${notionPages.length} pages in Notion response`);

  if (notionPages.length === 0) {
    console.log('📭 No scheduled posts found. Workflow will finish gracefully.');
    return [];
  }

  for (const data of notionPages) {
    console.log('\n=== 📋 Processing page ===');

    const postTitle = data.properties.Nome?.title?.[0]?.text?.content || 'Untitled Post';
    console.log(`📝 Post: "${postTitle}"`);
    console.log(`🆔 Page ID: ${data.id}`);

    // Check all conditions (EXACTLY like the working version)
    const status = data.properties.Status?.select?.name;
    console.log(`📊 Status: ${status}`);
    if (status !== 'Agendado') {
      console.log(`❌ SKIP: Status is "${status}", not "Agendado"`);
      continue;
    }

    const shouldPublish = data.properties['Publicar?']?.checkbox;
    console.log(`✅ Publicar?: ${shouldPublish}`);
    if (!shouldPublish) {
      console.log('❌ SKIP: Publicar? is false');
      continue;
    }

    const platforms = data.properties.Plataforma?.multi_select || [];
    const platformNames = platforms.map(p => p.name);
    console.log(`🎯 Platforms: [${platformNames.join(', ')}]`);
    const hasInstagram = platforms.some(p => p.name === 'Instagram');

    if (!hasInstagram) {
      console.log('❌ SKIP: No Instagram platform');
      continue;
    }

    // Check scheduled date/time (EXACTLY like the working version)
    const scheduledDateData = data.properties['Data do Agendamento']?.date;
    console.log(`📅 Scheduled date data:`, scheduledDateData);

    if (!scheduledDateData) {
      console.log('❌ SKIP: No scheduled date set');
      continue;
    }

    const scheduledStart = scheduledDateData.start;
    let scheduledDate;

    if (scheduledStart.includes('T')) {
      scheduledDate = new Date(scheduledStart);
    } else {
      scheduledDate = new Date(scheduledStart + 'T09:00:00');
    }

    console.log(`📅 Scheduled for: ${scheduledDate.toISOString()}`);
    console.log(`📅 Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

    const isTimeToPublish = now >= scheduledDate;
    console.log(`⏰ Time check: ${isTimeToPublish ? '✅ READY' : '❌ NOT YET'}`);

    if (!isTimeToPublish) {
      const timeUntil = scheduledDate - now;
      const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
      console.log(`⏳ SKIP: Still ${hoursUntil} hours until scheduled time`);
      continue;
    }

    // Extract post data (EXACTLY like the working version)
    const postText = data.properties['Texto do Post']?.rich_text?.[0]?.text?.content || '';
    const hashtags = data.properties.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';
    const media = data.properties.Mídia?.files?.[0];

    console.log(`📸 Media available: ${!!media}`);

    if (!media) {
      console.log(`❌ SKIP: Post "${postTitle}" has no media`);
      continue;
    }

    const originalUrl = media.external?.url || media.file?.url;
    console.log(`🔗 Original Media URL: ${originalUrl}`);

    // NEW: Prepare media URL and detect type
    const mediaUrl = prepareMediaUrl(originalUrl);
    const mediaType = getMediaType(mediaUrl);
    console.log(`🔗 Final Media URL: ${mediaUrl}`);
    console.log(`🎬 Media Type: ${mediaType}`);

    const caption = postText + (hashtags ? '\n\n' + hashtags : '');

    console.log(`🚀 POST APPROVED: "${postTitle}" ready for Instagram ${mediaType} posting!`);

    readyPosts.push({
      notion_page_id: data.id,
      post_title: postTitle,
      caption: caption,
      media_url: mediaUrl,
      media_type: mediaType,
      scheduled_date: scheduledDate.toISOString(),
      _preserve_notion_id: data.id,
      _preserve_title: postTitle
    });
  }
}

console.log(`\n🎯 === SIMPLE EXECUTION RESULT ===`);
console.log(`📊 Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('🚀 Posts that will be published NOW:');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('⏳ No posts ready to publish at this time. Next check in 30 seconds.');
}

return readyPosts.map(post => ({ json: post }));

// Recuperar notion_page_id dos dados originais
const currentData = $json;
const originalData = $('Process Posts (Simple)').item.json;

console.log('🔄 Recuperando notion_page_id...');

const finalData = {
  ...currentData,
  notion_page_id: originalData.notion_page_id,
  post_title: originalData.post_title,
  media_type: originalData.media_type,
  instagram_published: true,
  automated_publish: true,
  published_at: new Date().toISOString()
};

console.log('✅ Dados finais para Notion:', JSON.stringify(finalData, null, 2));

return { json: finalData };
