// PostFlow NATIVO OTIMIZADO - Com filtros do Notion e binary data direto
const items = $input.all();
const readyPosts = [];

console.log(`🔥 NATIVE OPTIMIZED - Processing ${items.length} items (filtered by Notion: Status=Agendado, Publicar?=true, Plataforma=Instagram, Data<=hoje)`);

// Get current date/time for comparison
const now = new Date();
console.log(`🕒 Current time: ${now.toISOString()}`);
console.log(`🌍 Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type from binary data
function getMediaTypeFromBinary(binaryData, fileName) {
  // Check mime type first (most reliable)
  if (binaryData && binaryData.mimeType) {
    const mimeType = binaryData.mimeType.toLowerCase();
    if (mimeType.startsWith('video/')) {
      console.log(`🎬 Detected VIDEO from mime type: ${mimeType}`);
      return 'VIDEO';
    }
    if (mimeType.startsWith('image/')) {
      console.log(`📸 Detected IMAGE from mime type: ${mimeType}`);
      return 'IMAGE';
    }
  }

  // Fallback to filename extension
  if (fileName) {
    const name = fileName.toLowerCase();
    const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];

    for (const ext of videoExtensions) {
      if (name.includes(ext)) {
        console.log(`🎬 Detected VIDEO from filename: ${fileName}`);
        return 'VIDEO';
      }
    }
  }

  console.log(`📸 Detected IMAGE (default): ${fileName}`);
  return 'IMAGE';
}

for (const item of items) {
  const data = item.json;
  const binaryData = item.binary || {};

  console.log('\n=== 🔥 Processing Notion page (NATIVE OPTIMIZED) ===');

  const postTitle = data.properties?.Nome?.title?.[0]?.text?.content || 'Untitled Post';
  console.log(`📝 Post: "${postTitle}"`);
  console.log(`🆔 Page ID: ${data.id}`);

  // Status já foi filtrado pelo Notion, mas vamos verificar
  const status = data.properties?.Status?.select?.name;
  const shouldPublish = data.properties?.['Publicar?']?.checkbox;
  const platforms = data.properties?.Plataforma?.multi_select || [];

  console.log(`📊 Status: ${status}`);
  console.log(`✅ Publicar?: ${shouldPublish}`);
  console.log(`🎯 Platforms: [${platforms.map(p => p.name).join(', ')}]`);

  // Check scheduled date/time (ainda precisamos validar o horário)
  const scheduledDateData = data.properties?.['Data do Agendamento']?.date;
  console.log(`📅 Scheduled date data:`, scheduledDateData);

  if (!scheduledDateData) {
    console.log('❌ SKIP: No scheduled date set');
    continue;
  }

  const scheduledStart = scheduledDateData.start;
  let scheduledDate;

  if (scheduledStart.includes('T')) {
    scheduledDate = new Date(scheduledStart);
  } else {
    scheduledDate = new Date(scheduledStart + 'T09:00:00');
  }

  console.log(`📅 Scheduled for: ${scheduledDate.toISOString()}`);
  console.log(`📅 Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

  const isTimeToPublish = now >= scheduledDate;
  console.log(`⏰ Time check: ${isTimeToPublish ? '✅ READY' : '❌ NOT YET'}`);

  if (!isTimeToPublish) {
    const timeUntil = scheduledDate - now;
    const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
    console.log(`⏳ SKIP: Still ${hoursUntil} hours until scheduled time`);
    continue;
  }

  // Extract post data
  const postText = data.properties?.['Texto do Post']?.rich_text?.[0]?.text?.content || '';
  const hashtags = data.properties?.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';

  // Check for binary data (files downloaded by Notion)
  console.log(`📦 Binary data keys:`, Object.keys(binaryData));

  if (Object.keys(binaryData).length === 0) {
    console.log(`❌ SKIP: Post "${postTitle}" has no binary data`);
    continue;
  }

  // Process first binary file (usually 'data' key)
  const firstBinaryKey = Object.keys(binaryData)[0];
  const mediaData = binaryData[firstBinaryKey];

  if (!mediaData) {
    console.log(`❌ SKIP: No media data found in binary`);
    continue;
  }

  const fileName = mediaData.fileName || mediaData.name || 'media_file';
  const fileSize = mediaData.fileSize || 0;
  const mimeType = mediaData.mimeType || 'unknown';

  console.log(`📁 Binary file: ${fileName}`);
  console.log(`📊 File info: ${mimeType}, ${Math.round(fileSize/1024/1024*100)/100} MB`);

  // Detect media type from binary data
  const mediaType = getMediaTypeFromBinary(mediaData, fileName);
  const caption = postText + (hashtags ? '\n\n' + hashtags : '');

  console.log(`🔥 POST APPROVED: "${postTitle}" ready for native Instagram ${mediaType} posting!`);
  console.log(`📁 File: ${fileName} (${Math.round(fileSize/1024/1024*100)/100}MB)`);

  readyPosts.push({
    page_id: data.id,
    post_title: postTitle,
    caption: caption,
    file_name: fileName,
    media_type: mediaType,
    file_size: fileSize,
    mime_type: mimeType,
    scheduled_date: scheduledDate.toISOString(),
    binary_key: firstBinaryKey
  });
}

console.log(`\n🔥 === NATIVE OPTIMIZED RESULT ===`);
console.log(`📊 Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('🚀 Posts that will be published NOW (OPTIMIZED):');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (${Math.round(post.file_size/1024/1024*100)/100}MB) (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('⏳ No posts ready to publish at this time. Next check in 30 seconds.');
}

return readyPosts.map(post => ({
  json: post,
  binary: {
    data: $input.item(readyPosts.indexOf(post)).binary[post.binary_key]
  }
}));
