// PostFlow NATIVO - Processamento com Notion integrado
const items = $input.all();
const readyPosts = [];

console.log(`ğŸ”¥ NATIVE NOTION - Processing ${items.length} items`);

// Get current date/time for comparison
const now = new Date();
console.log(`ğŸ•’ Current time: ${now.toISOString()}`);
console.log(`ğŸŒ Current time (Local): ${now.toLocaleString('pt-BR')}`);

// Helper function to detect media type from file info
function getMediaTypeFromFile(fileInfo) {
  if (!fileInfo || !fileInfo.name) {
    return 'IMAGE'; // default
  }

  const fileName = fileInfo.name.toLowerCase();
  const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];

  for (const ext of videoExtensions) {
    if (fileName.includes(ext)) {
      console.log(`ğŸ¬ Detected VIDEO from filename: ${fileName}`);
      return 'VIDEO';
    }
  }

  console.log(`ğŸ“¸ Detected IMAGE from filename: ${fileName}`);
  return 'IMAGE';
}

for (const item of items) {
  const data = item.json;

  console.log('\n=== ğŸ”¥ Processing Notion page (NATIVE) ===');

  const postTitle = data.properties?.Nome?.title?.[0]?.text?.content || 'Untitled Post';
  console.log(`ğŸ“ Post: "${postTitle}"`);
  console.log(`ğŸ†” Page ID: ${data.id}`);

  // Check scheduled date/time (lÃ³gica que jÃ¡ funciona)
  const scheduledDateData = data.properties?.['Data do Agendamento']?.date;
  console.log(`ğŸ“… Scheduled date data:`, scheduledDateData);

  if (!scheduledDateData) {
    console.log('âŒ SKIP: No scheduled date set');
    continue;
  }

  const scheduledStart = scheduledDateData.start;
  let scheduledDate;

  if (scheduledStart.includes('T')) {
    scheduledDate = new Date(scheduledStart);
  } else {
    scheduledDate = new Date(scheduledStart + 'T09:00:00');
  }

  console.log(`ğŸ“… Scheduled for: ${scheduledDate.toISOString()}`);
  console.log(`ğŸ“… Scheduled for (Local): ${scheduledDate.toLocaleString('pt-BR')}`);

  const isTimeToPublish = now >= scheduledDate;
  console.log(`â° Time check: ${isTimeToPublish ? 'âœ… READY' : 'âŒ NOT YET'}`);

  if (!isTimeToPublish) {
    const timeUntil = scheduledDate - now;
    const hoursUntil = Math.round(timeUntil / (1000 * 60 * 60) * 10) / 10;
    console.log(`â³ SKIP: Still ${hoursUntil} hours until scheduled time`);
    continue;
  }

  // Extract post data
  const postText = data.properties?.['Texto do Post']?.rich_text?.[0]?.text?.content || '';
  const hashtags = data.properties?.Hashtags?.multi_select?.map(tag => tag.name.startsWith('#') ? tag.name : `#${tag.name}`).join(' ') || '';
  const mediaFiles = data.properties?.MÃ­dia?.files || [];

  console.log(`ğŸ“¸ Media files found: ${mediaFiles.length}`);

  if (mediaFiles.length === 0) {
    console.log(`âŒ SKIP: Post "${postTitle}" has no media files`);
    continue;
  }

  // Process first media file
  const mediaFile = mediaFiles[0];
  console.log(`ğŸ“ Processing media file:`, mediaFile);

  // Check if it's uploaded to Notion or external
  let fileUrl = null;
  let fileName = null;
  let isNotionFile = false;

  if (mediaFile.file) {
    // File uploaded to Notion
    fileUrl = mediaFile.file.url;
    fileName = mediaFile.name || 'media_file';
    isNotionFile = true;
    console.log(`ğŸ“¤ Notion hosted file: ${fileName}`);
  } else if (mediaFile.external) {
    // External file (Google Drive, etc.)
    fileUrl = mediaFile.external.url;
    fileName = mediaFile.name || 'external_file';
    isNotionFile = false;
    console.log(`ğŸ”— External file: ${fileName}`);
  }

  if (!fileUrl) {
    console.log(`âŒ SKIP: Could not determine file URL`);
    continue;
  }

  // Detect media type
  const mediaType = getMediaTypeFromFile({ name: fileName });
  const caption = postText + (hashtags ? '\n\n' + hashtags : '');

  console.log(`ğŸ”¥ POST APPROVED: "${postTitle}" ready for native Instagram ${mediaType} posting!`);
  console.log(`ğŸ“ File: ${fileName} (${isNotionFile ? 'Notion hosted' : 'External'})`);

  readyPosts.push({
    page_id: data.id,
    post_title: postTitle,
    caption: caption,
    file_url: fileUrl,
    file_name: fileName,
    media_type: mediaType,
    is_notion_file: isNotionFile,
    scheduled_date: scheduledDate.toISOString(),
    // Preserve original data
    _original_page_data: data
  });
}

console.log(`\nğŸ”¥ === NATIVE NOTION RESULT ===`);
console.log(`ğŸ“Š Total posts ready for Instagram: ${readyPosts.length}`);
if (readyPosts.length > 0) {
  console.log('ğŸš€ Posts that will be published NOW (NATIVE):');
  readyPosts.forEach((post, i) => {
    console.log(`   ${i+1}. "${post.post_title}" [${post.media_type}] (${post.is_notion_file ? 'Notion' : 'External'}) (scheduled: ${new Date(post.scheduled_date).toLocaleString('pt-BR')})`);
  });
} else {
  console.log('â³ No posts ready to publish at this time. Next check in 30 seconds.');
}

return readyPosts.map(post => ({ json: post }));

// Debug: Log the data being passed to the download phase
const items = $input.all();

console.log('\n=== ğŸ” DEBUG: Before Download Phase ===');
console.log(`Items received: ${items.length}`);

for (const item of items) {
  const data = item.json;
  console.log(`\nğŸ“‹ Processing item for download:`);
  console.log(`  ğŸ“ Title: ${data.post_title}`);
  console.log(`  ğŸ”— File URL: ${data.file_url}`);
  console.log(`  ğŸ“ File Name: ${data.file_name}`);
  console.log(`  ğŸ­ Media Type: ${data.media_type}`);
  console.log(`  ğŸ“¤ Is Notion File: ${data.is_notion_file}`);

  // Check if URL is valid
  if (!data.file_url) {
    console.log('  âŒ ERROR: No file URL found!');
  } else if (data.file_url.length < 10) {
    console.log('  âš ï¸  WARNING: File URL seems too short!');
  } else {
    console.log('  âœ… File URL looks valid');
  }
}

console.log('=== ğŸ” DEBUG: End of Debug ===\n');

return items;

// Debug: Log the data after download
const items = $input.all();

console.log('\n=== ğŸ” DEBUG: After Download Phase ===');
console.log(`Items received after download: ${items.length}`);

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  console.log(`\nğŸ“‹ Item ${i + 1}:`);

  // Check if we have binary data
  if (item.binary) {
    console.log(`  ğŸ“¦ Binary data keys: ${Object.keys(item.binary)}`);
    for (const key of Object.keys(item.binary)) {
      const binaryData = item.binary[key];
      console.log(`    ğŸ“ Binary '${key}': ${binaryData.mimeType}, ${binaryData.fileSize} bytes`);
    }
  } else {
    console.log(`  âŒ No binary data found!`);
  }

  // Check JSON data
  if (item.json) {
    console.log(`  ğŸ“‹ JSON keys: ${Object.keys(item.json)}`);
    console.log(`  ğŸ“ Title: ${item.json.post_title}`);
    console.log(`  ğŸ­ Media Type: ${item.json.media_type}`);
  } else {
    console.log(`  âŒ No JSON data found!`);
  }
}

console.log('=== ğŸ” DEBUG: End After Download ===\n');

return items;

// Recuperar page_id dos dados originais
const currentData = $json;
const originalData = $('Process Posts (Native)').item.json;

console.log('ğŸ”„ Recuperando page_id...');
console.log('Media type:', originalData.media_type);
console.log('Source:', originalData.is_notion_file ? 'Notion' : 'External');

const finalData = {
  ...currentData,
  page_id: originalData.page_id,
  post_title: originalData.post_title,
  media_type: originalData.media_type,
  source_type: originalData.is_notion_file ? 'NOTION' : 'EXTERNAL',
  instagram_published: true,
  automated_publish: true,
  native_integration: true,
  published_at: new Date().toISOString()
};

console.log('âœ… Dados finais para Notion (NATIVE):', JSON.stringify(finalData, null, 2));

return { json: finalData };
